import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

function ParticleSphere() {
  // Drone Model Component
  function DroneModel() {
    const { scene } = useGLTF('/drone.glb'); // Load the 3D model
    return <primitive object={scene} scale={0.5} />; // Render the model
  }

  return (
    <div className="min-h-screen bg-gray-900 flex flex-col items-center justify-center p-8">
      {/* Heading */}
      <h1 className="text-4xl font-bold text-white mb-4">Explore Our Drone Technology</h1>

      {/* Description */}
      <p className="text-lg text-gray-300 mb-8 text-center max-w-2xl">
        Discover the future of aerial innovation with our advanced drones, designed to push the boundaries of what's possible in flight and photography.
      </p>

      {/* 3D Drone Model */}
      <div className="w-full h-[400px] md:h-[600px] bg-gray-800 rounded-lg shadow-2xl overflow-hidden">
        <Canvas camera={{ position: [5, 5, 5], fov: 25 }}>
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} />
          <DroneModel />
          <OrbitControls enableZoom={true} />
        </Canvas>
      </div>

      {/* Call to Action Button */}
      <button className="mt-8 px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition duration-300">
        Learn More
      </button>
    </div>
  );
}

export default ParticleSphere;
