import React, { useState, useRef } from "react";
import { Link } from "react-router-dom";
import logo from "../../assets/logo.png";

const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [mobileDropdownOpen, setMobileDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  // Close dropdown if clicking outside
  const handleClickOutside = (event) => {
    if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
      setDropdownOpen(false);
    }
  };

  React.useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <header className="bg-black shadow-sm">
      <div className="max-w-screen-xl mx-auto px-4 py-4 flex items-center justify-between">
        {/* Logo */}
        <Link to="/" className="flex items-center space-x-3">
          <img
            src={logo}
            alt="Drone Cast Logo"
            className="w-16 h-16 object-contain"
          />
          <span className="text-lg font-semibold text-gray-50">Drone Cast</span>
        </Link>

        {/* Desktop Navigation */}
        <nav className="hidden lg:flex space-x-6 relative z-50">
          <CustomLink to="/">HOME</CustomLink>
          <CustomLink to="/Realisation">NOS RÉALISATIONS</CustomLink>

          {/* Services Dropdown */}
          <div
            className="relative group"
            ref={dropdownRef}
            onMouseEnter={() => setDropdownOpen(true)}
            onMouseLeave={() => setDropdownOpen(false)}
          >
            <button
              className="text-sm font-medium text-gray-50 hover:text-redOne flex items-center relative"
              onClick={() => setDropdownOpen(!dropdownOpen)}
            >
              NOS SERVICES
              <svg
                className="ml-1 w-4 h-4"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" />
              </svg>
            </button>

            {dropdownOpen && <DropdownContent />}
          </div>

          <CustomLink to="/light-show">SPECTACLE DE LUMIÈRE</CustomLink>
          <CustomLink to="/Aboutus">A PROPOS</CustomLink>
          <CustomLink to="/contact">CONTACT</CustomLink>
        </nav>

        {/* Mobile Menu Toggle */}
        <button
          className="lg:hidden text-gray-50"
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
        >
          <svg
            className="w-6 h-6"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7" />
          </svg>
        </button>
      </div>

      {/* Mobile Navigation */}
      {isMobileMenuOpen && (
        <nav className="lg:hidden bg-white border-t border-gray-200 shadow-md">
          <ul className="flex flex-col space-y-2 p-4">
            <li><Link className="text-gray-900 text-sm font-medium hover:text-redOne" to="/">HOME</Link></li>
            <li><Link className="text-gray-900 text-sm font-medium hover:text-redOne" to="/Realisation">NOS RÉALISATIONS</Link></li>
            <li>
              <button
                className="text-sm font-medium text-gray-900 hover:text-redOne flex items-center"
                onClick={() => setMobileDropdownOpen(!mobileDropdownOpen)}
              >
                NOS SERVICES
                <svg
                  className="ml-1 w-4 h-4"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" />
                </svg>
              </button>
              {mobileDropdownOpen && <DropdownContent />}
            </li>
            <li><Link className="text-gray-900 text-sm font-medium hover:text-redOne" to="/Aboutus">A PROPOS</Link></li>
            <li><Link className="text-gray-900 text-sm font-medium hover:text-redOne" to="/light-show">SPECTACLE DE LUMIÈRE</Link></li>
            <li><Link className="text-gray-900 text-sm font-medium hover:text-redOne" to="/contact">CONTACT</Link></li>
          </ul>
        </nav>
      )}
    </header>
  );
};

// Custom Link Component
const CustomLink = ({ to, children }) => (
  <Link
    to={to}
    className="text-sm font-medium text-white hover:text-redOne relative group"
  >
    {children}
    <span
      className="absolute bottom-0 left-0 w-0 h-0.5 bg-redOne transition-all duration-300 group-hover:w-full"
    />
  </Link>
);

// Dropdown Content Component
const DropdownContent = () => (
  <div className="absolute left-0 bg-white border border-gray-300 shadow-lg rounded-lg z-50 w-[300px] mt-2 overflow-hidden">
    <ul className="space-y-1">
      {[
        { path: "/Agriculture", label: "Agriculture de précision" },
        { path: "/Topographique", label: "Levée Topographique par drone" },
        { path: "/Cartographie", label: "Cartographie Aérienne par drone" },
        { path: "/Surveillance", label: "Surveillance & Inspection industrielle" },
        { path: "/Thermographie", label: "Thermographie par drone" },
        { path: "/Amenagement", label: "Aménagement du territoire" },
        { path: "/Mines", label: "Mines & Carrières" },
        { path: "/Nettoyage", label: "Démoussage ou Nettoyage par drone" },
      ].map((item) => (
        <li key={item.path} className="group">
          <Link
            to={item.path}
            className="block px-4 py-2 text-sm font-medium text-gray-800 rounded-md transition-colors duration-200 ease-in-out hover:bg-redOne hover:text-white"
          >
            {item.label}
          </Link>
        </li>
      ))}
    </ul>
  </div>
);

export default Header;
