import React, { useRef, useState } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { OrbitControls, useGLTF } from "@react-three/drei";
import gsap from "gsap";

// Drone model loader
const DroneModel = React.forwardRef(({ scale, visible }, ref) => {
  const { scene } = useGLTF("/drone.glb");

  useFrame(() => {
    if (scene) {
      scene.rotation.y += 0.01; // Slow rotation for visual effect
    }
  });

  return (
    <primitive
      ref={ref}
      object={scene}
      scale={scale}
      visible={visible}
      position={[0, 0, 0]}
      rotation={[0, 3.14, 0]}
    />
  );
});

const ParticleSphere = () => {
  const [showText, setShowText] = useState(false);
  const droneRef = useRef();
  const [phase, setPhase] = useState(0);

  // GSAP animation to control the drone model scale and camera position
  useFrame(({ clock }) => {
    const t = clock.getElapsedTime();
    
    if (phase === 0) {
      if (droneRef.current.scale.x > 0.1) {
        droneRef.current.scale.x -= 0.05;
        droneRef.current.scale.y -= 0.05;
        droneRef.current.scale.z -= 0.05;
      } else {
        setPhase(1); // Move to next phase after animation
      }
    }
    
    if (phase === 1) {
      if (t > 1) {
        setShowText(true); // Show text after animation completes
      }
    }
  });

  return (
    <div className="bg-black relative flex justify-center items-center w-full h-screen">
      <Canvas
        shadows
        camera={{ position: [0, 0, 10] }}
        style={{ height: "100%", touchAction: "none" }}
      >
        <OrbitControls enableZoom={false} enableRotate={false} enablePan={false} makeDefault />
        <ambientLight intensity={0.4} />
        <directionalLight position={[10, 10, 10]} intensity={1.5} castShadow />
        <DroneModel ref={droneRef} scale={[1, 1, 1]} visible={phase === 0 || phase === 1} />
      </Canvas>

      {/* Title, Description, and Button */}
      {showText && (
        <div className="absolute text-center">
          <h1 className="mb-4 text-4xl sm:text-5xl md:text-6xl lg:text-7xl xl:text-8xl text-gray-50 font-bold">
            EXPÉRIENCE AÉRIENNE
          </h1>
          <p className="mb-6 text-sm sm:text-base md:text-lg text-gray-50 lg:text-xl xl:text-2xl">
            Des performances de drones spectaculaires aux services pratiques, pour répondre à vos besoins industriels, agricoles, et créatifs.
          </p>
          <button className="bg-redOne px-6 py-3 rounded-full group">
            <span className="text-white pr-5">
              <a href="/contact">Contact-nous</a>
            </span>
            <span className="absolute text-white inset-y-0 right-4 flex items-center transition-transform duration-300">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="w-5 h-5">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7" />
              </svg>
            </span>
          </button>
        </div>
      )}
    </div>
  );
};

export default ParticleSphere;
