import React, { useState, useEffect, useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { OrbitControls, useGLTF } from "@react-three/drei";
import { Link } from "react-router-dom";

const DroneModel = React.forwardRef(({ scale, visible, position }, ref) => {
  const { scene } = useGLTF("/drone.glb");

  useFrame(() => {
    if (scene) {
      scene.rotation.y += 0.01;  // Rotate the drone a bit for animation
    }
  });

  return (
    <primitive
      ref={ref}
      object={scene}
      scale={scale}
      visible={visible}
      position={position} 
    />
  );
});

const ParticleSphere = () => {
  const [showText, setShowText] = useState(false);
  const [dronePosition, setDronePosition] = useState([0, 0, 0]);

  const droneRef = useRef();

  useEffect(() => {
    setTimeout(() => {
      setDronePosition([30, 0, 0]);  // Move the drone to the right after some time
      setShowText(true);  // Show the text
    }, 2000);  // 2 seconds delay for the transition
  }, []);

  return (
    <div className="bg-black relative flex justify-center items-center w-full h-screen">
      <div style={{ width: "100%", height: "100%" }}>
        <Canvas
          shadows
          camera={{ position: [0, 0, 75] }}
          style={{ height: "100%", touchAction: "none" }}
        >
          <OrbitControls enableZoom={false} enableRotate={false} enablePan={false} makeDefault />
          <ambientLight intensity={0.4} />
          <directionalLight
            position={[10, 10, 10]}
            intensity={1.5}
            castShadow
            shadow-mapSize-width={1024}
            shadow-mapSize-height={1024}
          />
          <spotLight
            position={[-10, 15, 10]}
            angle={0.3}
            penumbra={1}
            intensity={2}
            castShadow
          />
          <DroneModel
            ref={droneRef}
            scale={[40, 40, 40]}
            visible={true}
            position={dronePosition}
          />
        </Canvas>
      </div>

      {/* Title, Description, and Button (left side) */}
      {showText && (
        <div className="absolute left-5 top-1/2 transform -translate-y-1/2 text-left">
          <h1
            className="mb-4 text-4xl sm:text-5xl md:text-6xl lg:text-7xl xl:text-8xl text-gray-50 font-bold"
            style={{
              fontFamily: "Cabinet Grotesk, sans-serif",
              textShadow: "2px 2px 4px rgba(0, 0, 0, 0.5)",
            }}
          >
            EXPÉRIENCE AÉRIENNE
          </h1>
          <p
            className="mb-6 text-sm sm:text-base md:text-lg text-gray-50 lg:text-xl xl:text-2xl"
            style={{
              fontFamily: "Roboto, sans-serif",
              textShadow: "1px 1px 3px rgba(0, 0, 0, 0.4)",
            }}
          >
            Des performances de drones spectaculaires aux services pratiques, pour répondre à vos besoins industriels, agricoles, et créatifs.
          </p>
          <button className="bg-redOne px-6 py-3 rounded-full group focus:outline-none focus:ring-4 transition-all ml-5">
            <span className="text-white pr-5"><Link to="/contact">Contact-nous</Link></span>
            <span className="absolute text-white inset-y-0 right-4 flex items-center transition-transform duration-300 translate-x-0 group-hover:translate-x-2">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                className="w-5 h-5"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M9 5l7 7-7 7"
                />
              </svg>
            </span>
          </button>
        </div>
      )}
    </div>
  );
};

export default ParticleSphere;
