import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { init } from "../../utils/initDroneLayout";

const ParticleSphere = () => {
  const [showText, setShowText] = useState(false); // Manage visibility of text

  useEffect(() => {
    // Initialize the drone animation
    init();

    // Show text after the animation has been loaded/initialized
    const timer = setTimeout(() => {
      setShowText(true);
    }, 3000); // Adjust the timeout as needed

    // Cleanup the timer
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="bg-black relative flex justify-between items-center w-full h-screen">
      {/* Left Side (Text and Title) */}
      <div className="flex flex-col items-start justify-center w-1/2 p-6">
        {showText && (
          <>
            <h1
              className="mb-4 text-4xl sm:text-5xl md:text-6xl lg:text-7xl xl:text-8xl text-gray-50 font-bold"
              style={{
                fontFamily: "Cabinet Grotesk, sans-serif",
                textShadow: "2px 2px 4px rgba(0, 0, 0, 0.5)",
              }}
            >
              EXPÉRIENCE AÉRIENNE
            </h1>
            <p
              className="mb-6 text-sm sm:text-base md:text-lg text-gray-50 lg:text-xl xl:text-2xl"
              style={{
                fontFamily: "Roboto, sans-serif",
                textShadow: "1px 1px 3px rgba(0, 0, 0, 0.4)",
              }}
            >
              Des performances de drones spectaculaires aux services pratiques, pour répondre à vos besoins industriels, agricoles, et créatifs.
            </p>
            <button className="bg-redOne relative px-6 py-3 rounded-full group focus:outline-none focus:ring-4 transition-all ml-5">
              <span className="text-white pr-5">
                <Link to="/contact">Contact-nous</Link>
              </span>
              <span className="absolute text-white inset-y-0 right-4 flex items-center transition-transform duration-300 translate-x-0 group-hover:translate-x-2">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  className="w-5 h-5"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M9 5l7 7-7 7"
                  />
                </svg>
              </span>
            </button>
          </>
        )}
      </div>

      {/* Right Side (3D Drone Canvas) */}
      <div className="w-1/2 h-full">
        <div
          style={{ width: "100%", height: "100%" }}
          id="canvas-container"
        >
          {/* This is where the Three.js renderer will be attached */}
        </div>
      </div>

      {/* Gradient Background at the Bottom */}
      <div
        className="absolute bottom-0 left-0 w-full h-1/3"
        style={{
          background: 'linear-gradient(to top, #000000, rgba(0, 0, 0, 0))', // Gradient from black to transparent
        }}
      ></div>
    </div>
  );
};

export default ParticleSphere;
