import React, { useState } from "react";
import { Link } from "react-router-dom";
import logo from "../../assets/logo.png";

const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [mobileDropdownOpen, setMobileDropdownOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
    setMobileDropdownOpen(false); // Close mobile dropdown when toggling menu
  };

  const handleDropdownHover = (isOpen) => {
    setDropdownOpen(isOpen);
  };

  const handleMobileDropdownClick = () => {
    setMobileDropdownOpen(!mobileDropdownOpen);
  };

  return (
    <header className="bg-black shadow-sm">
      <div className="max-w-screen-xl mx-auto px-4 py-4 flex items-center justify-between">
        {/* Logo */}
        <Link to="/" className="flex items-center space-x-3">
          <img
            src={logo}
            alt="Drone Cast Logo"
            className="w-16 h-16 object-contain"
          />
          <span className="text-lg font-semibold text-gray-50">Drone Cast</span>
        </Link>

        {/* Desktop Navigation */}
        <nav className="hidden lg:flex space-x-6 relative z-50">
          <CustomLink to="/">HOME</CustomLink>
          <CustomLink to="/Realisation">NOS RÉALISATIONS</CustomLink>

          {/* Dropdown */}
          <div
            className="relative"
            onMouseEnter={() => handleDropdownHover(true)}
            onMouseLeave={() => handleDropdownHover(false)}
          >
            <button
              className="text-sm font-medium text-gray-50 hover:text-redOne flex items-center relative focus:outline-none"
              onClick={() => setDropdownOpen(!dropdownOpen)}
              aria-expanded={dropdownOpen}
              aria-haspopup="true"
            >
              NOS SERVICES
              <svg
                className="ml-1 w-4 h-4"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" />
              </svg>
              <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-redOne transition-all duration-300 group-hover:w-full" />
            </button>
            {dropdownOpen && (
              <DropdownContent
                onMouseEnter={() => handleDropdownHover(true)}
                onMouseLeave={() => handleDropdownHover(false)}
              />
            )}
          </div>
          <CustomLink to="/light-show">SPECTACLE DE LUMIÈRE</CustomLink>
          <CustomLink to="/Aboutus">A PROPOS</CustomLink>
          <CustomLink to="/contact">CONTACT</CustomLink>
        </nav>

        {/* Desktop Social Media Icons */}
        <div className="hidden lg:flex space-x-4">
          <SocialIcon href="https://facebook.com" iconClass="fab fa-facebook-f" />
          <SocialIcon href="https://instagram.com" iconClass="fab fa-instagram" />
          <SocialIcon href="https://tiktok.com" iconClass="fab fa-tiktok" />
        </div>

        {/* Mobile Menu Toggle */}
        <button
          className="lg:hidden text-gray-50 focus:outline-none"
          onClick={toggleMobileMenu}
          aria-label="Toggle mobile menu"
        >
          <svg
            className="w-6 h-6"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7" />
          </svg>
        </button>
      </div>

      {/* Mobile Navigation */}
      {isMobileMenuOpen && (
        <nav className="lg:hidden bg-white border-t border-gray-200 shadow-md">
          <ul className="flex flex-col space-y-2 p-4">
            <MobileLink to="/">HOME</MobileLink>
            <MobileLink to="/Realisation">NOS RÉALISATIONS</MobileLink>
            <li>
              <button
                className="text-sm font-medium text-gray-900 hover:text-redOne flex items-center relative group focus:outline-none"
                onClick={handleMobileDropdownClick}
                aria-expanded={mobileDropdownOpen}
                aria-haspopup="true"
              >
                NOS SERVICES
                <svg
                  className="ml-1 w-4 h-4"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" />
                </svg>
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-redOne transition-all duration-300 group-hover:w-full" />
              </button>
              {mobileDropdownOpen && (
                <div className="mt-2 bg-white border border-gray-200 shadow-lg rounded-md">
                  <DropdownContent />
                </div>
              )}
            </li>
            <MobileLink to="/light-show">SPECTACLE DE LUMIÈRE</MobileLink>
            <MobileLink to="/Aboutus">A PROPOS</MobileLink>
            <MobileLink to="/contact">CONTACT</MobileLink>
          </ul>
        </nav>
      )}
    </header>
  );
};

const CustomLink = ({ to, children }) => (
  <Link
    to={to}
    className="text-sm font-medium text-white hover:text-redOne relative group focus:outline-none"
  >
    {children}
    <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-redOne transition-all duration-300 group-hover:w-full" />
  </Link>
);

const MobileLink = ({ to, children }) => (
  <li>
    <Link
      to={to}
      className="text-gray-900 text-sm font-medium hover:text-redOne relative group focus:outline-none"
    >
      {children}
      <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-redOne transition-all duration-300 group-hover:w-full" />
    </Link>
  </li>
);

const SocialIcon = ({ href, iconClass }) => (
  <a
    href={href}
    className="text-gray-50 hover:text-blue-600 focus:outline-none"
    target="_blank"
    rel="noopener noreferrer"
    aria-label={`Visit our ${iconClass.split('-')[1]} page`}
  >
    <i className={`${iconClass} text-xl`} />
  </a>
);

const DropdownContent = ({ onMouseEnter, onMouseLeave }) => (
  <div
    className="absolute left-0 bg-white border border-gray-300 shadow-lg rounded-lg z-50 w-[300px] mt-2 overflow-hidden"
    onMouseEnter={onMouseEnter}
    onMouseLeave={onMouseLeave}
  >
    <ul className="space-y-1">
      <DropdownItem to="/Agriculture">Agriculture de précision</DropdownItem>
      <DropdownItem to="/Topographique">Levée Topographique par drone</DropdownItem>
      <DropdownItem to="/Cartographie">Cartographie Aérienne par drone</DropdownItem>
      <DropdownItem to="/Surveillance">Surveillance & Inspection industrielle</DropdownItem>
      <DropdownItem to="/Thermographie">Thermographie par drone</DropdownItem>
      <DropdownItem to="/Amenagement">Aménagement du territoire</DropdownItem>
