import React, { useEffect, useRef } from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader.js";
import gsap from "gsap";

const ParticleSphere = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    // Initialize Three.js scene
    let camera, scene, renderer;
    const loader = new GLTFLoader();

    // Set up camera
    camera = new THREE.PerspectiveCamera(
      70,
      window.innerWidth / window.innerHeight,
      0.01,
      10
    );
    camera.position.z = 5;
    camera.rotation.z = 1;

    // Set up scene
    scene = new THREE.Scene();
    const light = new THREE.AmbientLight(0xffffff, 2);
    scene.add(light);

    // Load drone model
    loader.load("/models/drone/scene.gltf", (gltf) => {
      let model = gltf.scene;
      model.scale.set(0.45, 0.45, 0.45);

      // GSAP animations
      gsap.to(camera.position, {
        z: 1,
        duration: 1,
        ease: "back.out(1.7)",
      });
      gsap.to(camera.rotation, {
        z: 0,
        duration: 1,
      });
      gsap.to(model.rotation, {
        x: 1,
        duration: 1,
        delay: 1,
      });
      gsap.to(model.rotation, {
        y: Math.PI * 1.75,
        duration: 2,
        delay: 1,
      });
      gsap.to(model.scale, {
        delay: 1,
        duration: 1,
        x: 0.25,
        y: 0.25,
        z: 0.25,
      });
      gsap.to(model.position, {
        delay: 1,
        duration: 1,
        x: 0.35,
        y: 0.3,
      });

      scene.add(model);
    });

    // Set up renderer
    renderer = new THREE.WebGLRenderer({
      antialias: true,
      canvas: canvasRef.current,
    });
    renderer.setSize(window.innerWidth / 2, window.innerHeight); // Half width for the right side
    renderer.setClearColor(0x272727, 1);

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
    };
    animate();

    // Handle window resize
    const handleResize = () => {
      camera.aspect = (window.innerWidth / 2) / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth / 2, window.innerHeight);
    };
    window.addEventListener("resize", handleResize);

    // Cleanup
    return () => {
      window.removeEventListener("resize", handleResize);
      renderer.dispose();
    };
  }, []);

  return (
    <div className="min-h-screen bg-black text-white flex flex-col lg:flex-row">
      {/* Left Side: Text Content */}
      <div className="flex-1 flex flex-col justify-center items-start p-6 lg:p-12">
        <h1 className="text-4xl sm:text-5xl md:text-6xl font-bold mb-4">
          EXPÉRIENCE AÉRIENNE
        </h1>
        <p className="text-sm sm:text-base md:text-lg mb-6 max-w-2xl">
          Des performances de drones spectaculaires aux services pratiques, pour
          répondre à vos besoins industriels, agricoles, et créatifs.
        </p>
        <button className="bg-red-600 px-6 py-3 rounded-full hover:bg-red-700 transition-colors">
          Contactez-nous
        </button>
      </div>

      {/* Right Side: 3D Model */}
      <div className="flex-1 relative">
        <canvas
          ref={canvasRef}
          className="w-full h-full"
        />
      </div>
    </div>
  );
};

export default ParticleSphere;
