import React, { useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { OrbitControls, useGLTF } from "@react-three/drei";
import * as THREE from "three";
import { Link } from "react-router-dom";

const DroneModel = React.forwardRef(({ scale, position }, ref) => {
  const { scene } = useGLTF("/drone.glb");


  return (
    <primitive
      ref={ref}
      object={scene}
      scale={scale}
      position={position}
      rotation={[0, 3.14, 0]} // Initial rotation (optional)
    />
  );
});

const ParticleSphere = () => {
  const droneRef = useRef();

  return (
    <div className="bg-black relative flex justify-center items-center w-full h-screen">
      {/* Left Side: Text Content */}
      <div className="flex-1 flex flex-col justify-center items-start pl-12 text-white">
        <h1
          className="mb-4 text-4xl sm:text-5xl md:text-6xl lg:text-7xl xl:text-8xl font-bold"
          style={{
            fontFamily: "Cabinet Grotesk, sans-serif",
            textShadow: "2px 2px 4px rgba(0, 0, 0, 0.5)",
          }}
        >
          EXPÉRIENCE AÉRIENNE
        </h1>
        <p
          className="mb-6 text-sm sm:text-base md:text-lg lg:text-xl xl:text-2xl"
          style={{
            fontFamily: "Roboto, sans-serif",
            textShadow: "1px 1px 3px rgba(0, 0, 0, 0.4)",
          }}
        >
          Des performances de drones spectaculaires aux services pratiques, pour répondre à vos besoins industriels, agricoles, et créatifs.
        </p>
        <button className="bg-redOne relative px-6 py-3 rounded-full group focus:outline-none focus:ring-4 transition-all">
          <span className="text-white pr-5">
            <Link to="/contact">Contact-nous</Link>
          </span>
          <span className="absolute text-white inset-y-0 right-4 flex items-center transition-transform duration-300 translate-x-0 group-hover:translate-x-2">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
              className="w-5 h-5"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M9 5l7 7-7 7"
              />
            </svg>
          </span>
        </button>
      </div>

      {/* Right Side: 3D Model */}
      <div className="flex-1" style={{ height: "100vh" }}>
        <Canvas
          shadows
          camera={{ position: [0, 0, 75], fov: 50 }}
          style={{ height: "100%", touchAction: "none" }}
        >
          <ambientLight intensity={0.4} />
          <directionalLight
            position={[10, 10, 10]}
            intensity={1.5}
            castShadow
            shadow-mapSize-width={1024}
            shadow-mapSize-height={1024}
          />
          <spotLight
            position={[-10, 15, 10]}
            angle={0.3}
            penumbra={1}
            intensity={2}
            castShadow
          />
          <DroneModel
            ref={droneRef}
            scale={[20, 20, 20]}
            position={[10, 0, 0]} // Adjust position to the right
          />
          <OrbitControls enableZoom={false} enableRotate={true} enablePan={false} />
        </Canvas>
      </div>
    </div>
  );
};

export default ParticleSphere;
