import React, { useEffect, useState, useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { OrbitControls, useGLTF } from "@react-three/drei";
import gsap from "gsap";

const DroneModel = React.forwardRef(({ scale, visible, position }, ref) => {
  const { scene } = useGLTF("/drone.glb");

  useFrame(() => {
    if (scene) {
      scene.rotation.y += 0.01;  // Rotate the drone a bit
    }
  });

  return (
    <primitive
      ref={ref}
      object={scene}
      scale={scale}
      visible={visible}
      position={position}
      rotation={[0, Math.PI, 0]} // Adjust the rotation to match your model's orientation
    />
  );
});

const ParticleSphere = () => {
  const [phase, setPhase] = useState(0);  // To control animation phases
  const droneRef = useRef();
  const [showText, setShowText] = useState(false); // Controls when to show the text

  useEffect(() => {
    // Trigger animation for the drone
    if (phase === 0) {
      gsap.to(droneRef.current.position, {
        x: 10, // Slide the drone to the right
        duration: 2,  // Duration of the slide
        onComplete: () => setPhase(1), // Change phase once animation is done
      });

      setTimeout(() => {
        setShowText(true); // Show text after the drone animation completes
      }, 2000);  // Show text 2 seconds after the animation starts
    }
  }, [phase]);

  return (
    <div className="bg-black relative flex justify-center items-center w-full h-screen">
      {/* Canvas */}
      <div className="w-full h-full">
        <Canvas
          shadows
          camera={{ position: [0, 0, 30] }}
          style={{ height: "100%" }}
        >
          <OrbitControls enableZoom={false} enableRotate={false} enablePan={false} makeDefault />
          <ambientLight intensity={0.4} />
          <directionalLight position={[10, 10, 10]} intensity={1.5} castShadow />
          <spotLight position={[-10, 15, 10]} angle={0.3} penumbra={1} intensity={2} castShadow />
          <DroneModel
            ref={droneRef}
            scale={[2, 2, 2]}  // Scale your model
            visible={phase === 0 || phase === 1} // Only show during animation
            position={[0, 0, -10]} // Start position of the drone (center)
          />
        </Canvas>
      </div>

      {/* Title and Button */}
      {showText && (
        <div className="absolute left-10 text-left text-white">
          <h1
            className="text-4xl sm:text-5xl font-bold mb-4"
            style={{
              fontFamily: "Cabinet Grotesk, sans-serif",
              textShadow: "2px 2px 4px rgba(0, 0, 0, 0.5)",
            }}
          >
            EXPÉRIENCE AÉRIENNE
          </h1>
          <p
            className="text-lg sm:text-xl mb-6"
            style={{
              fontFamily: "Roboto, sans-serif",
              textShadow: "1px 1px 3px rgba(0, 0, 0, 0.4)",
            }}
          >
            Des performances de drones spectaculaires aux services pratiques.
          </p>
          <button className="bg-red-600 hover:bg-red-700 text-white px-6 py-3 rounded-full transition-all duration-300">
            Contact-nous
          </button>
        </div>
      )}
    </div>
  );
};

export default ParticleSphere;
